package p2024_07_03;

public class Oper09 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		// 복합 대입 연산자
		// +=, -=, *=, /=, %=
		
		int a = 10, b = 3;
		
		System.out.println(a+=b);  //a=a+b   a=13
		System.out.println(a-=b);  //a=a-b   a=10
		System.out.println(a*=b);  //a=a*b   a=30
		System.out.println(a/=b);  //a=a/b   a=10
		System.out.println(a%=b);  //a=a%b   a=1
	}

}
